USE nyc_taxi_projectDB;




select rate_code_id,rate_code
    from OPENROWSET(
        BULK 'raw/rate_code_multi_line.json',
        DATA_SOURCE =  'nyc_taxi_data',
        FORMAT = 'csv',
        FIELDQUOTE = '0x0b',
        FIELDTERMINATOR = '0x0b',
        ROWTERMINATOR = '0x0b' 
    )with(
        jsonmultiline NVARCHAR(MAX)
    )
    as result_json
    CROSS APPLY openjson(jsonmultiline) with(
        rate_code_id SMALLINT,
        rate_code VARCHAR(50)
    );
